    // 调整共享内存段的大小（如果需要）
    struct shmid_ds buf;
    if (shmctl(shmid, IPC_STAT, &buf) == -1) {
        perror("shmctl IPC_STAT");
        exit(EXIT_FAILURE);
    }

    buf.shm_segsz = 2048; // 新的大小
    if (shmctl(shmid, IPC_SET, &buf) == -1) {
        perror("shmctl IPC_SET");
        exit(EXIT_FAILURE);
    }

    // 使用共享内存...

    // 删除共享内存段
    if (shmctl(shmid, IPC_RMID, NULL) == -1) {
