            mapper.enable(SerializationFeature.INDENT_OUTPUT);
            Object jsonObject = mapper.readValue(pureJson, Object.class);
            String formattedJson = mapper.writerWithDefaultPrettyPrinter().writeValueAsString(jsonObject);

            // 将注释重新插入到格式化后的JSON中
            return formattedJson + "\n" + comments.toString();
        } catch (Exception e) {
            e.printStackTrace();
            return json; // 如果解析失败，返回原始JSON
        }
    }
}
