```javascript
function calculateProbabilities(arrays) {
  // 初始化存储每个元素总概率的对象
  const probabilities = {};

  // 遍历每一组数组
  arrays.forEach((group) => {
    // 遍历当前组中的每个元素
    group.forEach((element, index) => {
      // 计算当前元素的概率调整值
      // 排名越靠前（index越小），概率减少得越多
      // 排名越靠后（index越大），概率增加得越多
      const adjustment = (index - (group.length - 1) / 2); // 以中间为基准调整
      // 更新该元素的总概率
