public RedisTemplate<String, NdcpTaskDto> taskRedisTemplate() {
    RedisTemplate<String, NdcpTaskDto> template = new RedisTemplate<>();
    template.setHashValueSerializer(new Jackson2JsonRedisSerializer<>(NdcpTaskDto.class));
    return template;
}

@Bean
public RedisTemplate<String, NdcpCollectorDto> collectorRedisTemplate() {
    RedisTemplate<String, NdcpCollectorDto> template = new RedisTemplate<>();
    template.setHashValueSerializer(new Jackson2JsonRedisSerializer<>(NdcpCollectorDto.class));
    return template;
