const fs = require('fs');
const parser = require('@babel/parser');
const traverse = require('@babel/traverse').default;
const { parseComponent } = require('@vue/compiler-dom');

// 读取.vue文件内容
const vueFileContent = fs.readFileSync('path/to/your/file.vue', 'utf-8');

// 使用@vue/compiler-dom解析Vue组件
const parsed = parseComponent(vueFileContent);

// 遍历AST节点
traverse(parsed.script.content, {
  enter(path) {
    console.log(path.node);
  }
});

// 如果需要处理template部分，可以这样操作
if (parsed.template) {
  const templateAst = parser.parse(parsed.template.content, {
