const fs = require('fs');
const { parse } = require('@vue/compiler-sfc');
const { parse: babelParse } = require('@babel/parser');

function parseVueFile(filePath) {
  try {
    // 读取.vue文件内容
    const fileContent = fs.readFileSync(filePath, 'utf-8');

    // 使用@vue/compiler-sfc解析Vue文件
    const { descriptor } = parse(fileContent);

    const result = {
      template: null,
      script: null,
      style: null
    };

    // 解析template部分
    if (descriptor.template) {
      result.template = {
        content: descriptor.template.content,
        ast: descriptor.template.ast
      };
    }

    // 解析script部分
    if (descriptor.script) {
      const scriptContent = descriptor.script.content;
      const scriptAst = babelParse(scriptContent, {
        sourceType: 'module',
        plugins: ['jsx', 'typescript']
      });
      result.script = {
        content: scriptContent,
        ast: scriptAst
      };
    }

    // 解析style部分
    if (descriptor.styles.length > 0) {
      result.style = descriptor.styles.map(style => ({
        content: style.content,
        scoped: style.scoped,
        lang: style.lang
      }));
    }

    return result;

  } catch (error) {
    console.error('解析Vue文件时发生错误:', error);
    throw error;
  }
