function calculateProbabilities(arrays) {
  // 创建一个Map来存储每个元素的总概率
  const probabilityMap = new Map();

  // 初始化每个元素的基础概率为1
  const allElements = [...new Set(arrays.flat())];
  allElements.forEach(element => {
    probabilityMap.set(element, 0);
  });

  // 遍历每组数组
  arrays.forEach(group => {
    const length = group.length;

    // 处理每个元素在当前组中的概率
    group.forEach((element, index) => {
      // 基础概率为1
      let probability = 1;

      // 根据位置调整概率
      // 前半部分减少概率，后半部分增加概率
      const position = index + 1;
      const middlePosition = (length + 1) / 2;

      // 计算位置对概率的影响
      const positionEffect = (position - middlePosition) / middlePosition;
      probability += positionEffect;

      // 累加到该元素的总概率中
      probabilityMap.set(
        element, 
        probabilityMap.get(element) + probability
      );
    });
  });

  // 将Map转换为数组并排序
  const sortedResult = [...probabilityMap.entries()]
    .sort((a, b) => b[1] - a[1]) // 按概率降序排序
    .map(([element, probability]) => ({
      element,
      probability: Number(probability.toFixed(3)) // 保留3位小数
    }));

  return sortedResult;
}

