import java.util.*;

public class Solution {
    private List<List<Integer>> allResults;
    private int targetK;

    public int mergeWays(int[] arr, int k) {
        this.allResults = new ArrayList<>();
        this.targetK = k;

        // 将数组转换为List，方便操作
        List<Integer> current = new ArrayList<>();
        for (int num : arr) {
            current.add(num);
        }

        // 开始递归
        dfs(current);

        // 返回符合条件的结果数量
        return allResults.size();
    }

    private void dfs(List<Integer> current) {
        // 如果当前数组已经不能再合并，检查是否符合条件
        if (current.size() == 1) {
            if (current.get(0) >= targetK) {
                allResults.add(new ArrayList<>(current));
            }
            return;
        }

        // 尝试合并相邻的元素
        for (int i = 0; i < current.size() - 1; i++) {
            int sum = current.get(i) + current.get(i + 1);

            // 创建新的列表存储合并后的结果
            List<Integer> next = new ArrayList<>();
            // 添加合并位置之前的元素
            for (int j = 0; j < i; j++) {
                next.add(current.get(j));
            }
            // 添加合并后的元素
            next.add(sum);
            // 添加合并位置之后的元素
            for (int j = i + 2; j < current.size(); j++) {
                next.add(current.get(j));
            }

            // 递归处理
            dfs(next);
        }
    }

