const fs = require('fs');
const parser = require('@babel/parser');
const traverse = require('@babel/traverse').default;
const { parseComponent } = require('@vue/compiler-dom');

// 读取.vue文件内容
const vueFileContent = fs.readFileSync('path/to/your/file.vue', 'utf-8');

// 使用@vue/compiler-dom解析Vue组件
