```c
#include <stdio.h>
#include <stdlib.h> // 如果需要支持动态内存分配

int main() {
    int n;

    // 输入数组的大小
    printf("请输入数组长度: ");
    scanf("%d", &n);

    if (n <= 0) {
        printf("数组长度必须为正数！\n");
        return 1;
    }

    // 如果需要动态内存分配，可使用 malloc：int *num3 = (int *)malloc(n * sizeof(int));
    // 如果使用固定大小数组，确保输入 n 不超过范围
    if (n > 200) {
        printf("数组长度不能超过200！\n");
        return 1;
    }

    int num3[200]; // 定义一个固定大小的数组，大小为200

    // 输入数组元素
    printf("请输入数组元素:\n");
    for (int i = 0; i < n; i++) {
        scanf("%d", &num3[i]);
    }

    // 选择排序
    for (int i = 0; i < n - 1; i++) {
        int minIndex = i;  // 假定当前下标为最小值索引

        // 找到未排序部分中的最小值
        for (int j = i + 1; j < n; j++) {
            if (num3[j] < num3[minIndex]) {  
