```swift
func formatPhoneNumber(_ phoneNumber: String) -> String {
    // 去除非数字字符
    let cleaned = phoneNumber.replacingOccurrences(of: "[^0-9]", with: "", options: .regularExpression, range: nil)

    // 检查长度是否足够
    guard cleaned.count == 11 else { return phoneNumber }

    // 分割字符串
    let startIndex = cleaned.startIndex
    let firstPart = String(cleaned[startIndex..<cleaned.index(startIndex, offsetBy: 3)])
    let secondPart = String(cleaned[cleaned.index(startIndex, offsetBy: 3)..<cleaned.index(startIndex, offsetBy: 7)])
    let thirdPart = String(cleaned[cleaned.index(startIndex, offsetBy: 7)...])

    // 组合
    return "\(firstPart) \(secondPart) \(thirdPart)"
}

